create database water_margin;

CREATE TABLE characters (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    ten VARCHAR(50),
    nickname VARCHAR(50),
    biet_danh VARCHAR(50),
    biet_danh_giai_nghia VARCHAR(100),
    type_of_destiny_star VARCHAR(15),
    destiny_title VARCHAR(40),
    sao_chieu_menh VARCHAR(40),
    rank VARCHAR(10),
    position_en VARCHAR(60),
    chuc_vu_vai_tro VARCHAR(60),
    is_historical_status VARCHAR(15),
    merits_activities TEXT
);


